pragma solidity 0.6.12;
// a library for performing overflow-safe math, updated with awesomeness from of DappHub (https://github.com/dapphub/ds-math)
library BoringMath {
    function add(uint256 a, uint256 b) internal pure returns (uint256 c) {require((c = a + b) >= b, "BoringMath: Add Overflow");}
    function sub(uint256 a, uint256 b) internal pure returns (uint256 c) {require((c = a - b) <= a, "BoringMath: Underflow");}
    function mul(uint256 a, uint256 b) internal pure returns (uint256 c) {require(b == 0 || (c = a * b)/b == a, "BoringMath: Mul Overflow");}
    function to128(uint256 a) internal pure returns (uint128 c) {
        require(a <= uint128(-1), "BoringMath: uint128 Overflow");
        c = uint128(a);
    }
}

library BoringMath128 {
    function add(uint128 a, uint128 b) internal pure returns (uint128 c) {require((c = a + b) >= b, "BoringMath: Add Overflow");}
    function sub(uint128 a, uint128 b) internal pure returns (uint128 c) {require((c = a - b) <= a, "BoringMath: Underflow");}
}","keccak256":"0x7fd07387abfcf6627a8fc6ab5d5e1238ae0832923fbc1065c2039161ac949e4f","license":"MIT"},"@bartjman/boring-solidity/contracts/libraries/BoringRebase.sol":{"content":"// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.6.12;
import "./BoringMath.sol";

struct Rebase {
    uint128 amount;
    uint128 share;
}

library RebaseLibrary {
    using BoringMath for uint256;

    function toShare(Rebase memory total, uint256 amount) internal pure returns (uint256 share) {
        share = total.amount == 0 ? amount : amount.mul(total.share) / total.amount;
    }

    function toAmount(Rebase memory total, uint256 share) internal pure returns (uint256 amount) {
        amount = total.share == 0 ? share : share.mul(total.amount) / total.share;
    }
}","keccak256":"0x62aa3a0e069d8f0ad30acb321744fe4dea5e16bd8ac2a94f76993c6fd0aa7add","license":"UNLICENSED"},"contracts/BentoBoxPlus.sol":{"content":"// SPDX-License-Identifier: UNLICENSED

